package earth.praesideo.controllers.common;import earth.praesideo.controllers.BaseController;import earth.praesideo.database.entities.common.YearRepresentationEntity;import earth.praesideo.database.repositories.common.YearRepresentationRepository;import org.slf4j.Logger;import org.slf4j.LoggerFactory;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.http.ResponseEntity;import org.springframework.web.bind.annotation.GetMapping;import org.springframework.web.bind.annotation.RequestMapping;import org.springframework.web.bind.annotation.RestController;import java.util.List;@RestController@RequestMapping("/api/common")public class YearRepresentationRestController extends BaseController {    private static final Logger LOGGER = LoggerFactory.getLogger(YearRepresentationRestController.class);    @Autowired    YearRepresentationRepository yearRepository;    public YearRepresentationRestController() {        LOGGER.info(this.getClass().getSimpleName() + " created.");    }    @GetMapping("/year-representation/list")    public ResponseEntity<List<YearRepresentationEntity>> retrieve(){        return ResponseEntity.ok(yearRepository.findAll());    }}